        public Dictionary<string, string> GetAppLicenses()
        {
            var result = new Dictionary<string, string>(commonLicenses);

            foreach (var entry in GetPlatformSpecificLicenses())
            {
                result[entry.Key] = entry.Value;
            }

            return result;
        }

        protected abstract Dictionary<string, string> GetPlatformSpecificLicenses();
    }
